// This file is used to store the parameters of the geometric shape generated by the OpenAI API.

let shapeParameters = {
  type: null,
  dimensions: null,
  otherParameters: null,
};

// Function to update the shape parameters
const updateShapeParameters = (newParameters) => {
  shapeParameters = newParameters;
};

// Function to get the current shape parameters
const getShapeParameters = () => {
  return shapeParameters;
};

export { updateShapeParameters, getShapeParameters };
